
/* -------------------------------------------------------------
    Addition to the Pretty Photo plugin - handles hover state 
    on images linked to Pretty Photo
------------------------------------------------------------- */

jQuery.fn.prettyPhotoHover = function(conf) {

  conf = jQuery.extend({
    zoomIcon: 'img/icons/image_zoom.png',
    zoomIconWidth: 40,
    zoomIconHeight: 40,
    imageOpacity: 0.4,
    zoomIconOpacity: 0.75,
    animationSpeed: 400,
    imageFadeColor: '#bbc'
  }, conf);
  
  return this.each(function() {
    
    var prettyPhotoLink = jQuery(this);
    
    var zoomIcon = jQuery('<img/>')
      .attr({ 'src':conf.zoomIcon, 'class':'zoom-icon' })
      .css({ 'position':'absolute', 'top' : 0, 'left' : 0 })
      .fadeTo(0,0);
      
    var prettyPhotoItem = jQuery('<div class="prettyPhotoItem" style="background:' + conf.imageFadeColor + '"/>');
      
    prettyPhotoLink.children('img')
      .css('display','block')
      .addClass('prettyPhotoImg')
      .wrap(prettyPhotoItem);
      
    prettyPhotoLink.children('div.prettyPhotoItem')
      .append(zoomIcon)
      .hover( function() {
        var image = jQuery(this).children('img.prettyPhotoImg');
        var imageWidth = image.width();
        var imageHeight = image.height();
        var zoomImage = image.next('img');
        image.stop().fadeTo(conf.animationSpeed ,conf.imageOpacity);
        zoomImage.stop()
          .css({ 'top' : ((imageHeight - conf.zoomIconHeight) / 2), 'left' : ((imageWidth - conf.zoomIconWidth) / 2) })
          .fadeTo(conf.animationSpeed ,conf.zoomIconOpacity);
      }, function() {
        var image = jQuery(this).children('img.prettyPhotoImg');
        var zoomImage = image.next('img');
        image.stop().fadeTo(conf.animationSpeed ,1);
        zoomImage.stop().fadeTo(conf.animationSpeed ,0);
      });
  
  });

}


/* --------------------------------
    Creates sliding-door buttons
-------------------------------- */

jQuery.fn.pragmaButtons = function(conf) {

  conf = jQuery.extend({}, conf);
  
  return this.each(function() {
    var el = jQuery(this);
    
    // Get element class names and remove leading 'j'
    var classes = el.attr('class').split(' ');
    for ( var i in classes) { classes[i] = classes[i].slice(1); }
    
    if (el[0].tagName == 'A') {            // if element is a link
    
      // Add presentational HTML inside the <a> tag
      el.wrapInner('<span class="right"></span>')
        .prepend('<b class="left"></b>')
        .removeClass().addClass(classes.join(' '));
        
    } else if (el[0].tagName == 'INPUT') { // if element is an input field
      var type = el.attr('type');
      
      // Replace the input field with an <a> tag containing presentational HTML inside
      var btn = jQuery('<a/>')
        .text(el.attr('value') || type)
        .wrapInner('<span class="right"></span>')
        .prepend('<b class="left"></b>')
        .addClass(classes.join(' '));
      el.replaceWith(btn);
      
      // Add click event to submit or reset the form
      var form = btn.closest('form')[0];
      if (form) {
        if (type == 'submit') {
          btn.click(function(){ form.submit(); return; });
        } else if (type == 'reset') {
          btn.click(function(){ form.reset(); return; });
        }
      } else {
        btn.click(function(){ return; });
      }
    }
    
  });
}


/* --------------------------------------------
    Navigation with multi-level drop-downs
-------------------------------------------- */

jQuery.fn.pragmaNavigation = function(conf) {

  conf = jQuery.extend({
    initialSubnavZIndex: 1000,          // zIndex for the 1st subnav, subsequent subnavs get incremental values
    slideDownSpeed: 400,                // time of the slide-down animation in miliseconds
    slideUpSpeed: 200,                  // time of the slide-up animation in miliseconds
    slideDownEffect: 'easeOutBounce',   // name of the easing function (look at the easing functions at the bottom of this file)
    slideUpEffect: 'easeOutQuart',      // name of the easing function (look at the easing functions at the bottom of this file)
    eventTimeoutTime: 200,              // delay before a mouse event is actioned
    subnavL1offsetTop: 32,              // distance of level 1 drop-down from the main nav link
    subnavL1offsetLeft: 13,
    subnavsOffsetTop: -10,              // distance of the child drop-down from the parent link
    subnavsOffsetLeft: 6,
    contentWidth: 960,                  // total width of the content area in pixels - used to decide whether a drop-down should be displayed on the right or left
    contentBoundryTolerance: 20,        // related to the above - sets how far the drop-downs can move outside the content area
    pointerDownImgNormal: 'img/interface/arrow-down-normal.gif',    // drop-down indicator images URL's
    pointerDownImgeHover: 'img/interface/arrow-down-hover.gif',
    pointerRightImgNormal: 'img/interface/arrow-right-normal.gif',
    pointerRightImgeHover: 'img/interface/arrow-right-hover.gif',
    pointerDownMarginTop: 8,            // drop-down indicator images positions
    pointerDownWidth: 7,
    pointerDownHeight: 4,
    pointerRightPosTop: 5,
    pointerRightPosLeft: 134
  }, conf);

  return this.each(function() {
    var nav = jQuery(this);
    var jqd = jQuery.data;
    var zIndex = conf.initialSubnavZIndex;
    var itemIndex = 0;
    var subnavIndex = 0;
    var level = 0;
    var parentItemUIDs = false;
    var parentSubnavUIDs = false;
    var items = new Object();
    var subnavs = new Object();
    var timeouts = new Object();
    
    nav.find('ul.sub-lev-1').wrap('<div class="main-nav-sub main-nav-sub-level-1 normal"><div class="inner"></div></div>');
    nav.find('ul.sub-lev-n').wrap('<div class="main-nav-sub main-nav-sub-level-n normal"><div class="inner"></div></div>');
    
    makeDropDowns(nav);
     
    function makeDropDowns (navLevel) {
      var levelItems = navLevel.children('div.inner').children('ul').children('li');
      levelItems.each(function(i){
        var item = jQuery(this);
        
        itemIndex++;
        var itemUID = 'item_'+itemIndex;
        jqd(item,'UID',itemUID);
        jqd(item,'level',level);
        items[itemUID] = item;
        
        if (item.find('ul.sub').size()>0) { // If item has subnav
          // Add subnav indicators
          if (level == 0) {
            var pointerDown = jQuery('<img/>')
                .attr({'src':getPointerImgSrc(level,'normal'),'width':conf.pointerDownWidth,'height':conf.pointerDownHeight})
                .css({'vertical-align':'top','margin-top':conf.pointerDownMarginTop});
            item.append(pointerDown);
          } else {
            var pointerRight = jQuery('<img/>')
                .attr({'src':getPointerImgSrc(level,'normal')})
                .css({'display':'block','position':'absolute','top':conf.pointerRightPosTop,'left':conf.pointerRightPosLeft});
            item.prepend(pointerRight);
          }
          var subnav = item.children('div.main-nav-sub');
          
          subnavIndex++;
          var subnavUID = 'sub_'+subnavIndex;
          jqd(item,'subnavUID',subnavUID);
          jqd(subnav,'UID',subnavUID);
          
          var level__tmp = level; level++;
          jqd(subnav,'level',level);
          
          var parentItemUIDs__tmp = parentItemUIDs;
          parentItemUIDs = ( parentItemUIDs ? parentItemUIDs+'|'+itemUID : itemUID );
          jqd(subnav,'parentItemUIDs',parentItemUIDs);
          
          jqd(subnav,'parentSubnavUIDs',parentSubnavUIDs);
          var parentSubnavUIDs__tmp = parentSubnavUIDs;
          parentSubnavUIDs = ( parentSubnavUIDs ? parentSubnavUIDs+'|'+subnavUID : subnavUID );
          
          subnavs[subnavUID] = subnav;
          initializeSubnav(item,subnav)
          itemAddMouseEvents(item,subnav);
          subnavAddMouseEvents(subnav);
          makeDropDowns(subnav);
          
          level = level__tmp; parentSubnavUIDs = parentSubnavUIDs__tmp; parentItemUIDs = parentItemUIDs__tmp;
        }
      });
    }
    
    function itemAddMouseEvents(item,subnav) {
      item.mouseenter(function(){
        var itemTimeoutKey = jqd(item,'UID');
        if (timeouts[itemTimeoutKey]) {
          clearTimeout(timeouts[itemTimeoutKey]);
        } else {
          item.addClass('item-hover');
          item.children('img').attr({'src':getPointerImgSrc(jqd(item,'level'),'hover')}).fadeTo('slow',0.5);
        }
        var subnavTimeoutKey = jqd(subnav,'UID');
        if (timeouts[subnavTimeoutKey]) {
          clearTimeout(timeouts[subnavTimeoutKey]);
        } else {
          positionSubnav(item,subnav);
          subnav.animate({height: "show"}, conf.slideDownSpeed, conf.slideDownEffect);
        }
      }).mouseleave(function(){
        var itemTimeout = setItemTimeout(item);
        var subnavTimeout = setSubnavTimeout(subnav);
        timeouts[ jqd(item,'UID') ] = itemTimeout;
        timeouts[ jqd(subnav,'UID') ] = subnavTimeout;
      });
    }
    
    function subnavAddMouseEvents(subnav) {
      subnav.mouseenter(function(){
        var parentItemUIDs = parentItems(subnav);
        for (var i=0; i < parentItemUIDs.length; i++) {
          var parentItemUID = parentItemUIDs[i];
          if (timeouts[parentItemUID]) { clearTimeout(timeouts[parentItemUID]); }
        }
        if (parentSubnavs(subnav)) {
          var parentSubnavUIDs = parentSubnavs(subnav);
          for (var i=0; i < parentSubnavUIDs.length; i++) {
            var parentSubnavUID = parentSubnavUIDs[i];
            clearTimeout(timeouts[parentSubnavUID]);
          }
        }
        if (timeouts[ jqd(subnav,'UID') ]) { clearTimeout(timeouts[ jqd(subnav,'UID') ]); }
      }).mouseleave(function(){
        var subnavTimeout = setSubnavTimeout(subnav);
        timeouts[ jqd(subnav,'UID') ] = subnavTimeout;
        var parentItemUIDs = parentItems(subnav);
        for (var i=0; i < parentItemUIDs.length; i++) {
          var parentItemUID = parentItemUIDs[i];
          var item = items[parentItemUID];
          var itemTimeout = setItemTimeout(item);
          timeouts[parentItemUID] = itemTimeout;
        }
        if (parentSubnavs(subnav)) {
          var parentSubnavUIDs = parentSubnavs(subnav);
          for (var i=0; i < parentSubnavUIDs.length; i++) {
            var parentSubnavUID = parentSubnavUIDs[i];
            var parentSubnav = subnavs[parentSubnavUID];
            var subnavTimeout = setSubnavTimeout(parentSubnav);
            timeouts[parentSubnavUID] = subnavTimeout;
          }
        }
      }); 
    }
    
    function  getPointerImgSrc (level,state) {
      if (level == 0) {
        if (state == 'hover') { return conf.pointerDownImgeHover; } else { return conf.pointerDownImgNormal; }
      } else {
        if (state == 'hover') { return conf.pointerRightImgeHover; } else { return conf.pointerRightImgNormal; }
      }
    }
    
    function parentItems (subnav) {
      return jqd(subnav,'parentItemUIDs').split('|');
    }
    
    function parentSubnavs (subnav) {
      if (jqd(subnav,'parentSubnavUIDs')) {
        return jqd(subnav,'parentSubnavUIDs').split('|');
      } else {
        return false;
      }
    }
    
    function setItemTimeout (item) {
      function timeoutFunc() {
        timeouts[jqd(item,'UID')] = undefined;
        item.removeClass('item-hover');
        item.children('img').attr({'src':getPointerImgSrc(jqd(item,'level'),'normal')}).fadeTo('slow',1);
      }
      return setTimeout(timeoutFunc, conf.eventTimeoutTime);
    }
    
    function setSubnavTimeout (subnav) {
      function timeoutFunc() {
        timeouts[jqd(subnav,'UID')] = undefined;
        subnav.animate({height: "hide"}, conf.slideUpSpeed, conf.slideUpEffect);

      }
      return setTimeout(timeoutFunc, conf.eventTimeoutTime);
    }
    
    function initializeSubnav (item,subnav) {
      subnav.remove().prependTo('body').css({ 'z-index':zIndex }).hide();
      zIndex++;
    }
    
    function positionSubnav(item,subnav) {
      var itemPos = item.offset();
      var itemWidth = item.outerWidth();
      var subnavWidth = subnav.outerWidth();
      if (jqd(subnav,'level') == 1) {
        var itemPosTop = itemPos.top + conf.subnavL1offsetTop; var itemPosLeft = itemPos.left + conf.subnavL1offsetLeft;
      } else {
        var itemPosTop = itemPos.top + conf.subnavsOffsetTop; var itemPosLeft = itemPos.left + itemWidth + conf.subnavsOffsetLeft;
        if ( (itemPosLeft + subnavWidth) > (contentBoundryRight()) ) {
          itemPosLeft = itemPos.left - subnavWidth - conf.subnavsOffsetLeft;
        }
      }
      
      subnav.css({ top: itemPosTop, left: itemPosLeft });
    }
    
    function contentBoundryRight(){
      var contentWidth = conf.contentWidth + (conf.contentBoundryTolerance * 2);
      var windowWidth = jQuery(window).width();
      if (windowWidth < contentWidth) {
        return windowWidth;
      } else {
        return ((windowWidth-contentWidth)/2)+contentWidth;
      }
    }
    
  });
}


/* ---------------------
    Content slider
--------------------- */

jQuery.fn.pragmaSlider = function(settings) {

  settings = jQuery.extend({
    slideSpeed: 900,                  // the time of the slide change animation in miliseconds
    slideEase: 'easeInOutExpo',       // the name of the easing function (look at the easing functions at the bottom of this file)
    sliderHeight: 'dynamic',          // can be set to 'dynamic', 'auto' or 'fixed'
    sliderHeightFixed: 280,           // the sider height in pixels, useful only if sliderHeight is set to 'fixed'
    slideBtnImgHeight: 95,            // height of the silder arrow right and arrow left images
    autoSlide: true,                  // 'true' - auto-slide active, 'false' - no auto-slide
    autoSlideSpeed: 3000,             // time interval between 2 slides when using auto-slide
    navButtons: true,                 // show or hide slider navigation buttons
    navButtonsPositionH: 'right',     // slider navigation horizontal position: 'right' or 'left'
    navButtonsPositionV: 'bottom',    // slider navigation vertical position: 'bottom' or 'top'
    firstSlide: 1                     // number of the slide to be showed first
  }, settings);  
  
  return this.each(function(){
    
    var slider = jQuery(this);
    var slidesStripe = slider.find('.slides');
    var slideWidth = slider.find('.slide:eq(0)').width();
    var slides = slider.find('.slide');
    var slidesCount = slides.size();
    var lastSlide = slidesCount-1;
    var clickFlag = false;
    
    slider.addClass('slider-active');
    
    // Set first slide
    if (settings.firstSlide != 1 && settings.firstSlide > 0 && settings.firstSlide <= slidesCount) { 
			var slideIndex = settings.firstSlide -1;
			slidesStripe.css({ marginLeft: slidesOffset(slideIndex) });
		} else { 
			var slideIndex = 0;
		};
    
    var currentSlide = getCurrentSlide(slideIndex);
    var sliderHeight = getSliderHeight(currentSlide);
     
    // Set slider width and height
    slidesStripe.css('width', slideWidth*slidesCount +25 + 'px');
    slidesStripe.css('height', sliderHeight + 'px');
    if (settings.sliderHeight=='auto') { slides.css('height', sliderHeight + 'px').css('width', slideWidth + 'px' ); }
    
    // Add next and previous buttons
    var prevSlideBtn = jQuery("<span/>")
        .attr({ 'class':'prev-slide' })
        .css('top', slideBtnPosition(sliderHeight))
        .click(function(){
          clickFlag=true;
          slideIndex = ( slideIndex == 0 ? lastSlide : --slideIndex );
          changeSlide(slideIndex);
        })
        .hover(function(){
          jQuery(this).css('background-position','0 -95px');
        },function(){
          jQuery(this).css('background-position','0 0');
        })
        .mouseup(function(){
          jQuery(this).css('background-position','0 -95px');
        }).mousedown(function(){
          jQuery(this).css('background-position','2px -95px');
        });
    var nextSlideBtn = jQuery("<span/>")
        .attr({ 'class':'next-slide' })
        .css('top', slideBtnPosition(sliderHeight))
        .click(function(){
          clickFlag=true;
          slideIndex = ( slideIndex == lastSlide ? 0 : ++slideIndex );
          changeSlide(slideIndex);
        })
        .hover(function(){
          jQuery(this).css('background-position','-20px -95px');
        },function(){
          jQuery(this).css('background-position','-20px 0');
        })
        .mouseup(function(){
          jQuery(this).css('background-position','-20px -95px');
        }).mousedown(function(){
          jQuery(this).css('background-position','-22px -95px');
        });
    slider.prepend(prevSlideBtn,nextSlideBtn);

    // Add navigation buttons
    if (settings.navButtons) {
      var navFloat = (settings.navButtonsPositionH == 'left') ? 'left' : 'right';
      var navButtons = jQuery("<div/>").attr({ 'class':'nav-buttons' });
      var navButtonsInner = jQuery("<div/>").attr({ 'class':'inner' }).css('width',slidesCount*20);
      if (settings.navButtonsPositionH=='left') { navButtonsInner.css('left','0'); } else { navButtonsInner.css('right','0'); }
      for ( var n=0; n<=lastSlide; n++ ) {
        var btn = jQuery("<span/>")
            .addClass('nav-btn slide-' + n + (n==0 ? ' first': '') + (n==lastSlide ? ' last': ''))
            .click(function(){
              clickFlag=true;
              var slideNumber = slideNumberFromClass(this.className);
              if (slideNumber) { slideIndex=slideNumber; changeSlide(slideIndex); }
            })
            .hover(function(){
              jQuery(this).addClass('hover');
            },function(){
              jQuery(this).removeClass('hover');
            })
            .mouseup(function(){
              jQuery(this).removeClass('active');
            }).mousedown(function(){
              jQuery(this).addClass('active');
            });;
        navButtonsInner.append(btn);
      }
      navButtons.append(navButtonsInner);
      if (settings.navButtonsPositionV == 'top') {
        slider.prepend(navButtons);
      } else {
        slider.append(navButtons);
      }
      navButtons.find('.slide-'+slideIndex).addClass('current');
    }
    
    // Start auto-slide
		if (settings.autoSlide) {
			slider.ready(function() {
				setTimeout(autoSlide,settings.autoSlideSpeed);
			});
		};
    
    
    // Functions
    
    function autoSlide() {
			if ( clickFlag==false ) {
        slideIndex = ( slideIndex == lastSlide ? 0 : ++slideIndex );
        changeSlide(slideIndex);
        setTimeout(autoSlide,settings.autoSlideSpeed);
      }
    }
    
    function slideNumberFromClass(cssClass) {
      var classList = cssClass.split(' ');
      for ( var i = 0; i < classList.length; i++ ) {
        if (classList[i].substr(0,6) == 'slide-') { return classList[i].substr(6)}
      }
      return false;
    }
    
    function getCurrentSlide(slideIndex) {
      return slider.find('.slide:eq(' + slideIndex + ')');
    };
    
    function slidesOffset(slideIndex) {
      return -(slideWidth*slideIndex);
    };
    
    function slideBtnPosition(sliderHeight) {
      return parseInt((sliderHeight - settings.slideBtnImgHeight)/2 + 10) + 'px';
    }
    
    function getSliderHeight(slide) {
      var sliderHeight = 0;
      switch (settings.sliderHeight) {
				case 'dynamic':
					sliderHeight = slide.height();
					break;
				case 'fixed':
					sliderHeight = settings.sliderHeightFixed;
					break;
        case 'auto':
          var tallestSlideHeight = 0
          slider.find('.slide').each(function(i){
            if (this.offsetHeight > tallestSlideHeight) { tallestSlideHeight = this.offsetHeight }
          });
          sliderHeight = tallestSlideHeight;
          break;
      }
      if (sliderHeight >= (settings.slideBtnImgHeight + 40)) {
        return sliderHeight;
      } else {
        return settings.slideBtnImgHeight + 40;
      }
    }
    
    function changeSlide(slideIndex) {
      currentSlide = getCurrentSlide(slideIndex);
      sliderHeight = getSliderHeight(currentSlide);
      var animationOptions = { marginLeft: slidesOffset(slideIndex) };
      if (settings.sliderHeight=='dynamic') {
        animationOptions = jQuery.extend({ height: sliderHeight }, animationOptions);
        prevSlideBtn.animate({ top: slideBtnPosition(sliderHeight) }, settings.slideSpeed, settings.slideEase);
        nextSlideBtn.animate({ top: slideBtnPosition(sliderHeight) }, settings.slideSpeed, settings.slideEase);
      }
      slidesStripe.animate(animationOptions, settings.slideSpeed, settings.slideEase );
      if (settings.navButtons) {
        navButtons.find('span').removeClass('current').end().find('.slide-'+slideIndex).addClass('current');
      }
    };
    
  });  
};



